import{M as e,O as t,h as n,p as r,u as i}from"./chunks/runtime-core.esm-bundler.CzgVbBaw.js";import{t as a}from"./chunks/plugin-vue_export-helper.Gez16YXs.js";const o=JSON.parse(`{"title":"Oxlint Beta","description":"","frontmatter":{"title":"Oxlint Beta","outline":"deep","authors":["boshen","camchenry"],"head":[["meta",{"property":"og:title","content":"Oxlint Beta"}],["meta",{"name":"twitter:title","content":"Oxlint Beta"}],["meta",{"property":"og:description","content":"A collection of high-performance JavaScript tools written in Rust"}],["meta",{"name":"twitter:description","content":"A collection of high-performance JavaScript tools written in Rust"}],["meta",{"property":"og:url","content":"https://oxc.rs/blog/2025-03-15-oxlint-beta.html"}]]},"headers":[],"relativePath":"blog/2025-03-15-oxlint-beta.md","filePath":"blog/2025-03-15-oxlint-beta.md"}`);var s={name:`blog/2025-03-15-oxlint-beta.md`};function c(a,o,s,c,l,u){let d=e(`AppBlogPostHeader`);return t(),i(`div`,null,[n(d),o[0]||=r(`<p>We are thrilled to announce that Oxlint is now in beta release, after more than a year of development by the community!</p><p>This milestone represents a significant step forward in feature completeness, performance, and stability.</p><h2 id="how-to-use" tabindex="-1">How to use <a class="header-anchor" href="#how-to-use" aria-label="Permalink to “How to use”">​</a></h2><p>At this stage, Oxlint can be used to fully replace ESLint in small to medium projects.</p><p>For larger projects, our advice is to turn off ESLint rules via <a href="https://www.npmjs.com/package/eslint-plugin-oxlint" target="_blank" rel="noreferrer">eslint-plugin-oxlint</a>, and run Oxlint before ESLint in your local or CI setup for a quicker feedback loop.</p><p>To test Oxlint in your codebase, you can use the package manager of your choice at the root of your codebase:</p><div class="vp-code-group"><div class="tabs"><input type="radio" name="group-20" id="tab-21" checked><label data-title="npm" for="tab-21">npm</label><input type="radio" name="group-20" id="tab-22"><label data-title="pnpm" for="tab-22">pnpm</label><input type="radio" name="group-20" id="tab-23"><label data-title="yarn" for="tab-23">yarn</label><input type="radio" name="group-20" id="tab-24"><label data-title="bun" for="tab-24">bun</label><input type="radio" name="group-20" id="tab-25"><label data-title="deno" for="tab-25">deno</label></div><div class="blocks"><div class="language-sh active"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">$</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> npx</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> oxlint@latest</span></span></code></pre></div><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">$</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> pnpm</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> dlx</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> oxlint@latest</span></span></code></pre></div><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">$</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> yarn</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> dlx</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> oxlint@latest</span></span></code></pre></div><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">$</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> bunx</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> oxlint@latest</span></span></code></pre></div><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">$</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> deno</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> run</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> npm:oxlint@latest</span></span></code></pre></div></div></div><p>For more detailed instructions on how to use Oxlint and integrate it with your project or editor, check out the <a href="/docs/guide/usage/linter.html">installation guide</a>.</p><h2 id="highlight-of-new-features-since-general-availability" tabindex="-1">Highlight of new features since general availability <a class="header-anchor" href="#highlight-of-new-features-since-general-availability" aria-label="Permalink to “Highlight of new features since general availability”">​</a></h2><ul><li>Significant performance improvements, up to twice as fast compared to the previous release</li><li>Includes over 500 rules built-in, with no extra installation required</li><li>Many rules from the <code>typescript</code>, <code>unicorn</code>, <code>react</code>, <code>react-perf</code>, <code>nextjs</code>, <code>import</code>, <code>jsdoc</code>, <code>jsx-a11y</code>, <code>node</code>, <code>promise</code>, <code>jest</code>, <code>vitest</code> plugins are now complete.</li><li>Support for configuration via <a href="/docs/guide/usage/linter/nested-config.html#configuration-file-format"><code>.oxlintrc.json</code> files</a>, including <a href="/docs/guide/usage/linter/nested-config.html">nested configuration files</a>, using <code>extends</code> to share configuration between files, applying <code>overrides</code> to specific files, and more.</li><li>Supports linting <code>&lt;script&gt;</code> content in <code>.astro</code>, <code>.svelte</code> and <code>.vue</code> files with no additional configuration needed.</li><li>Allows applying <a href="/docs/guide/usage/linter/automatic-fixes.html">automatic fixes</a> and suggestions to files.</li></ul><h2 id="more-rules-more-performance" tabindex="-1">More rules, more performance <a class="header-anchor" href="#more-rules-more-performance" aria-label="Permalink to “More rules, more performance”">​</a></h2><p>We have focused on making Oxlint more feature complete, supporting many of the most commonly used ESLint rules and plugins, but we have also made Oxlint much faster as well.</p><p>The first <a href="./2023-12-12-announcing-oxlint.html">generally available (GA) release of Oxlint</a> had 205 rules in total, with 70 of those being enabled by default. This beta release now includes 502 rules in total, with 99 of those being enabled by default (a 41% increase in the number of rules enabled by default).</p><table tabindex="0"><thead><tr><th>Rule type</th><th>Rule count (GA)</th><th>Rule count (beta)</th><th>Increase</th></tr></thead><tbody><tr><td>Default rules</td><td>70</td><td>99</td><td>+29 rules</td></tr><tr><td>Correctness</td><td>88</td><td>173</td><td>+88 rules</td></tr><tr><td>Performance</td><td>0</td><td>9</td><td>+9 rules</td></tr><tr><td>Restrictions</td><td>15</td><td>64</td><td>+49 rules</td></tr><tr><td>Pedantic</td><td>43</td><td>79</td><td>+36 rules</td></tr><tr><td>Style</td><td>38</td><td>137</td><td>+99 rules</td></tr><tr><td>Suspicious</td><td>7</td><td>28</td><td>+21 rules</td></tr><tr><td><strong>Total rules</strong></td><td>205</td><td><strong>502</strong></td><td><strong>+297 rules</strong></td></tr></tbody></table><p>Despite adding many new rules that are enabled by default, Oxlint is now much faster than it ever has been. Here are some <a href="https://gist.github.com/camchenry/cb09f6fae14ec1e3df1f72938b7350c8" target="_blank" rel="noreferrer">benchmarks</a> on some popular repositories:</p><table tabindex="0"><thead><tr><th>Repository</th><th>File count</th><th>Lint time (GA)</th><th>Lint time (beta)</th><th>Speedup</th></tr></thead><tbody><tr><td><code>elastic/kibana</code></td><td>68,591</td><td>6.02s</td><td><strong>3.11s</strong></td><td><strong>1.94x</strong></td></tr><tr><td><code>microsoft/vscode</code></td><td>5,703</td><td>1.697s</td><td><strong>0.792s</strong></td><td><strong>2.14x</strong></td></tr><tr><td><code>vitest-dev/vitest</code></td><td>1,732</td><td>105ms</td><td><strong>50ms</strong></td><td><strong>2.1x</strong></td></tr><tr><td><code>vuejs/core</code></td><td>1,063</td><td>217ms</td><td><strong>89ms</strong></td><td><strong>2.44x</strong></td></tr></tbody></table><h2 id="roadmap" tabindex="-1">Roadmap <a class="header-anchor" href="#roadmap" aria-label="Permalink to “Roadmap”">​</a></h2><p>One of the most commonly requested features for Oxlint is support for existing custom ESLint plugins. We have been busy working on the prerequisites for this feature, and to enable fast linter plugins written in JavaScript. We hope to have this feature available for the next major release, and more information to share about it in the near future.</p><p>We also are planning to continue improving the IDE/editor integrations, with improved support for VSCode, Zed, <code>coc.nvim</code>, and IntelliJ plugins.</p><h2 id="acknowledgements" tabindex="-1">Acknowledgements <a class="header-anchor" href="#acknowledgements" aria-label="Permalink to “Acknowledgements”">​</a></h2><p>Oxlint beta would not have been possible without the over 200 contributors to the project.</p><p>Special thanks goes to:</p><ul><li><a href="https://github.com/camc314" target="_blank" rel="noreferrer">@cam314</a>, <a href="https://github.com/mysteryven" target="_blank" rel="noreferrer">@mysteryven</a>, and <a href="https://github.com/shulaoda" target="_blank" rel="noreferrer">@shulaoda</a> for implementing many sophisticated lint rules, testing, and constantly improving everything.</li><li><a href="https://github.com/Sysix" target="_blank" rel="noreferrer">@Sysix</a> for maintaining <code>eslint-plugin-oxlint</code>.</li><li><a href="https://github.com/DonIsaac" target="_blank" rel="noreferrer">@DonIsaac</a> for improving configuration, documentation and website, and for representing Oxc at <a href="https://2024.squiggleconf.com" target="_blank" rel="noreferrer">SquiggleConf 2024</a>.</li><li><a href="https://github.com/leaysgur" target="_blank" rel="noreferrer">@leaysgur</a> for the RegExp parser and JSDoc plugin.</li><li><a href="https://github.com/u9g" target="_blank" rel="noreferrer">@u9g</a> and <a href="https://github.com/rzvxa" target="_blank" rel="noreferrer">@rzvxa</a> for implementing control flow graph analysis.</li><li><a href="https://github.com/branchseer" target="_blank" rel="noreferrer">@branchseer</a> for implementing the multi-file analysis runtime.</li><li><a href="https://github.com/camchenry" target="_blank" rel="noreferrer">@camchenry</a> for implementing nested configuration support.</li></ul>`,23)])}var l=a(s,[[`render`,c]]);export{o as __pageData,l as default};