import{M as e,O as t,h as n,p as r,u as i}from"./chunks/runtime-core.esm-bundler.DOFm0H2A.js";import{t as a}from"./chunks/plugin-vue_export-helper.QTl8ui86.js";const o=JSON.parse(`{"title":"Oxfmt: Oxc Formatter Alpha","description":"","frontmatter":{"title":"Oxfmt: Oxc Formatter Alpha","outline":"deep","authors":["boshen","Dunqing","leaysgur"],"head":[["meta",{"property":"og:title","content":"Oxfmt: Oxc Formatter Alpha"}],["meta",{"name":"twitter:title","content":"Oxfmt: Oxc Formatter Alpha"}],["meta",{"property":"og:description","content":"A collection of high-performance JavaScript tools written in Rust"}],["meta",{"name":"twitter:description","content":"A collection of high-performance JavaScript tools written in Rust"}],["meta",{"property":"og:url","content":"https://oxc.rs/blog/2025-12-01-oxfmt-alpha.html"}]]},"headers":[],"relativePath":"blog/2025-12-01-oxfmt-alpha.md","filePath":"blog/2025-12-01-oxfmt-alpha.md"}`);var s={name:`blog/2025-12-01-oxfmt-alpha.md`};function c(a,o,s,c,l,u){let d=e(`AppBlogPostHeader`);return t(),i(`div`,null,[n(d),o[0]||=r(`<p>We’re excited to announce the alpha release of <strong>Oxfmt</strong>, a Rust-powered, Prettier-compatible code formatter. This first release focuses on JavaScript and TypeScript, with support for additional languages coming soon.</p><p>Oxfmt is designed with these goals in mind:</p><ul><li><strong>Performance:</strong> More than 30× faster than Prettier and more than 3× faster than Biome on an initial run without a cache (<a href="https://github.com/oxc-project/bench-formatter" target="_blank" rel="noreferrer"><strong>benchmark</strong></a>).</li><li><strong>Compatibility:</strong> Prettier-compatible, so you can adopt Oxfmt in existing projects easily.</li><li><strong>Developer Experience:</strong> Upcoming features include import sorting, expanded formatting options, and support for Prettier plugins.</li></ul><h2 id="quick-start" tabindex="-1"><strong>Quick Start</strong> <a class="header-anchor" href="#quick-start" aria-label="Permalink to “Quick Start”">​</a></h2><p>Add <code>oxfmt</code> to your project:</p><div class="vp-code-group"><div class="tabs"><input type="radio" name="group-31" id="tab-32" checked><label data-title="npm" for="tab-32">npm</label><input type="radio" name="group-31" id="tab-33"><label data-title="pnpm" for="tab-33">pnpm</label><input type="radio" name="group-31" id="tab-34"><label data-title="yarn" for="tab-34">yarn</label><input type="radio" name="group-31" id="tab-35"><label data-title="bun" for="tab-35">bun</label><input type="radio" name="group-31" id="tab-36"><label data-title="deno" for="tab-36">deno</label></div><div class="blocks"><div class="language-sh active"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">npm</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> add</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -D</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> oxfmt@latest</span></span></code></pre></div><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">pnpm</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> add</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -D</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> oxfmt@latest</span></span></code></pre></div><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">yarn</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> add</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -D</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> oxfmt@latest</span></span></code></pre></div><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">bun</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> add</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -D</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> oxfmt@latest</span></span></code></pre></div><div class="language-sh"><button title="Copy Code" class="copy"></button><span class="lang">sh</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">deno</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> add</span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;"> -D</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> npm:oxfmt@latest</span></span></code></pre></div></div></div><p>Oxfmt follows Prettier’s configuration format. If you are using Prettier with a JSON configuration file, you can rename it to <code>.oxfmtrc.jsonc</code>:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">cp</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> .prettierrc.json</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> .oxfmtrc.jsonc</span></span></code></pre></div><p>You can also start from this <code>.oxfmtrc.jsonc</code> configuration example:</p><div class="language-jsx"><button title="Copy Code" class="copy"></button><span class="lang">jsx</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">{</span></span>
<span class="line"><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">  &quot;$schema&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;./node_modules/oxfmt/configuration_schema.json&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">,</span></span>
<span class="line"><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">  // Use 80 if migrating from Prettier; 100 is the Oxfmt default!</span></span>
<span class="line"><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">  &quot;printWidth&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">80</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">,</span></span>
<span class="line"><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">  &quot;ignorePatterns&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: [] </span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">// Same as \`.prettierignore\`</span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">}</span></span></code></pre></div><p>Next, add <code>oxfmt</code> to your package.json scripts:</p><div class="language-jsx"><button title="Copy Code" class="copy"></button><span class="lang">jsx</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;scripts&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: {</span></span>
<span class="line"><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">  &quot;format&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;oxfmt&quot;</span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">}</span></span></code></pre></div><p>Alternatively, refer to the\xA0<a href="/docs/guide/usage/formatter.html">installation guide</a>\xA0for detailed instructions.</p><h2 id="performance" tabindex="-1">Performance <a class="header-anchor" href="#performance" aria-label="Permalink to “Performance”">​</a></h2><p>Oxfmt is incredibly fast. Our benchmark results on the <a href="https://github.com/outline/outline" target="_blank" rel="noreferrer">Outline</a> repository show:</p><ul><li>Over 30× faster than Prettier’s experimental CLI without cache</li><li>Over 20× faster than Prettier using Oxc’s parser through <code>@prettier/plugin-oxc</code></li><li>Over 3× faster than Biome, another Rust-based formatter</li></ul><p>For detailed benchmark setup, please refer to the following repository:</p><blockquote><p>oxc-project/bench-formatter<br><a href="https://github.com/oxc-project/bench-formatter" target="_blank" rel="noreferrer">https://github.com/oxc-project/bench-formatter</a></p></blockquote><h2 id="design" tabindex="-1">Design <a class="header-anchor" href="#design" aria-label="Permalink to “Design”">​</a></h2><p>The Oxc team prioritizes compatibility with existing ecosystems, making migrations straightforward, even for large codebases.</p><h3 id="code-formatting-results" tabindex="-1">Code formatting results <a class="header-anchor" href="#code-formatting-results" aria-label="Permalink to “Code formatting results”">​</a></h3><p>Oxfmt matches Prettier’s JavaScript formatting. If you are migrating to Oxfmt today, you shouldn’t see any formatting differences compared to Prettier.</p><p>You might see minor differences when migrating from older versions of Prettier (<a href="https://github.com/SBoudrias/Inquirer.js/pull/1912" target="_blank" rel="noreferrer">See an example migration</a>) because we identified areas in which Prettier’s formatting could be improved. We have been actively collaborating with the Prettier team by submitting bug reports and pull requests directly to Prettier. Many of these improvements landed in the recent <a href="https://prettier.io/blog/2025/11/27/3.7.0" target="_blank" rel="noreferrer">Prettier 3.7</a> release.</p><p>Oxfmt currently passes around <a href="https://github.com/oxc-project/oxc/tree/main/tasks/prettier_conformance/snapshots" target="_blank" rel="noreferrer">95%</a> of Prettier’s JavaScript and TypeScript tests, and we hope to work with the Prettier team to converge on formatting over time.</p><h3 id="printwidth-100-by-default" tabindex="-1"><code>printWidth: 100</code> by default <a class="header-anchor" href="#printwidth-100-by-default" aria-label="Permalink to “printWidth: 100 by default”">​</a></h3><p>We chose <code>printWidth: 100</code> as the default line length instead of Prettier&#39;s <code>80</code>. Our reasons include:</p><ul><li>TypeScript code tends to be longer than JavaScript due to type annotations.</li><li>Import statements often contain many items.</li><li>Larger screens provide more horizontal space.</li><li>Results in slightly fewer LLM tokens.</li></ul><p>While Oxfmt remains compatible with Prettier, it uses a different default print width of 100 characters. If you want to avoid large diffs when migrating from Prettier, explicitly set the print width to 80.</p><h3 id="ignorepatterns" tabindex="-1"><code>ignorePatterns</code> <a class="header-anchor" href="#ignorepatterns" aria-label="Permalink to “ignorePatterns”">​</a></h3><p>While Oxfmt supports <code>.prettierignore</code>, it also supports an <code>ignorePatterns</code> configuration option to consolidate all configuration into a single file.</p><h3 id="configuration" tabindex="-1">Configuration <a class="header-anchor" href="#configuration" aria-label="Permalink to “Configuration”">​</a></h3><p>Prettier JSON configuration files are compatible with Oxfmt. In the simplest case, migrating your config looks like this:</p><div class="language-bash"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">cp</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> .prettierrc.json</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> .oxfmtrc.jsonc</span></span></code></pre></div><p>If your editor supports the JSON language server, you can start with this minimal template after adding <code>oxfmt</code> to your <code>devDependencies</code>:</p><div class="language-json"><button title="Copy Code" class="copy"></button><span class="lang">json</span><pre class="shiki shiki-themes github-light github-dark" style="--shiki-light:#24292e;--shiki-dark:#e1e4e8;--shiki-light-bg:#fff;--shiki-dark-bg:#24292e;" tabindex="0" dir="ltr"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">{</span></span>
<span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">  &quot;$schema&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;./node_modules/oxfmt/configuration_schema.json&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">,</span></span>
<span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">  &quot;ignorePatterns&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: []</span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">}</span></span></code></pre></div><p>While we don’t yet support all of Prettier’s configuration options in this alpha release, we do support the following major options: <code>printWidth</code>, <code>tabWidth</code>, <code>useTabs</code>, <code>semi</code>, <code>singleQuote</code>, <code>quoteProps</code>, <code>jsxSingleQuote</code>, <code>trailingComma</code>, <code>bracketSpacing</code>, <code>objectWrap</code>, <code>bracketSameLine</code>, <code>arrowParens</code>, <code>endOfLine</code>, and <code>singleAttributePerLine</code>.</p><p>Check out all of the options in our <a href="https://oxc.rs/docs/guide/usage/formatter.html#configuration-file" target="_blank" rel="noreferrer">documentation</a>.</p><h3 id="terminal-output" tabindex="-1">Terminal Output <a class="header-anchor" href="#terminal-output" aria-label="Permalink to “Terminal Output”">​</a></h3><p>Oxfmt&#39;s default behavior is equivalent to <code>prettier . --write</code>, providing the same UX as <code>cargo fmt</code> and producing no output. You can use <code>--check</code> to display formatting differences and enforce oxfmt usage in CI pipelines.</p><h2 id="beta-release-plans" tabindex="-1">Beta Release Plans <a class="header-anchor" href="#beta-release-plans" aria-label="Permalink to “Beta Release Plans”">​</a></h2><p>Here are our plans for the beta release:</p><ul><li><a href="https://github.com/oxc-project/oxc/issues/15899" target="_blank" rel="noreferrer">Support more file formats</a> - like <code>.json</code> files</li><li><a href="https://github.com/oxc-project/oxc/issues/15180" target="_blank" rel="noreferrer">Add support for embedded language formatting</a> - css in js or graphql in js</li><li><a href="https://github.com/oxc-project/oxc/issues/13610" target="_blank" rel="noreferrer">Built-in sorting and aesthetics features</a>, such as <a href="https://github.com/oxc-project/oxc/issues/14253" target="_blank" rel="noreferrer">sort imports</a></li><li><a href="https://github.com/oxc-project/oxc/issues/15665" target="_blank" rel="noreferrer">Prettier plugins</a></li><li><a href="https://github.com/oxc-project/oxc/issues/15913" target="_blank" rel="noreferrer">Node.js API for Oxfmt</a></li><li><a href="https://github.com/oxc-project/oxc/issues/15066" target="_blank" rel="noreferrer">Disable newline at the end of file</a></li><li><a href="https://github.com/oxc-project/oxc/issues/15849" target="_blank" rel="noreferrer"><code>--migrate prettier</code></a></li><li><em>… and your feature requests</em></li></ul><p>You can track our progress towards the beta release here:</p><blockquote><p>Formatter Beta · Milestone #15 · oxc-project/oxc<br><a href="https://github.com/oxc-project/oxc/milestone/15" target="_blank" rel="noreferrer">https://github.com/oxc-project/oxc/milestone/15</a></p></blockquote><p>We also plan to relax some of the formatting opinions in future versions.</p><h2 id="next-steps" tabindex="-1">Next Steps <a class="header-anchor" href="#next-steps" aria-label="Permalink to “Next Steps”">​</a></h2><p>See the full installation guide in the <a href="https://oxc.rs/docs/guide/usage/formatter.html" target="_blank" rel="noreferrer">Oxfmt docs</a>.</p><h3 id="reporting-issues" tabindex="-1">Reporting Issues <a class="header-anchor" href="#reporting-issues" aria-label="Permalink to “Reporting Issues”">​</a></h3><p>For formatting differences, please refer to <a href="https://github.com/oxc-project/oxc/discussions/14669" target="_blank" rel="noreferrer">https://github.com/oxc-project/oxc/discussions/14669</a>. Additionally, known issues are distinguished by <a href="https://github.com/oxc-project/oxc/issues?q=sort%3Aupdated-desc%20is%3Aissue%20label%3AA-formatter-prettier-diff" target="_blank" rel="noreferrer">labels</a>.</p><p>If you find any other issues, please create an issue with <a href="https://github.com/oxc-project/oxc/issues/new?template=formatter_diff_report.yaml" target="_blank" rel="noreferrer">dedicated template</a> on GitHub.</p><h3 id="join-the-community" tabindex="-1">Join the Community <a class="header-anchor" href="#join-the-community" aria-label="Permalink to “Join the Community”">​</a></h3><blockquote><p>RFC: Formatter · oxc-project/oxc · Discussion #13608<br><a href="https://github.com/oxc-project/oxc/discussions/13608" target="_blank" rel="noreferrer">https://github.com/oxc-project/oxc/discussions/13608</a></p></blockquote><p>We welcome your feedback to help make Oxfmt even better!</p><h2 id="acknowledgements" tabindex="-1">Acknowledgements <a class="header-anchor" href="#acknowledgements" aria-label="Permalink to “Acknowledgements”">​</a></h2><p>Oxfmt builds on a fork of the <a href="https://github.com/biomejs/biome/tree/main/crates/biome_formatter" target="_blank" rel="noreferrer"><code>biome_formatter</code></a> infrastructure, and we’d like to thank the Biome team, especially <a href="https://github.com/ematipico" target="_blank" rel="noreferrer">@ematipico</a> and <a href="https://github.com/MichaReiser" target="_blank" rel="noreferrer">@MichaReiser</a>. We’d also like to thank the Prettier team and <a href="https://github.com/fisker" target="_blank" rel="noreferrer">@fisker</a> for collaborating with us on formatting compatibility.</p>`,55)])}var l=a(s,[[`render`,c]]);export{o as __pageData,l as default};