import{M as e,f as t,g as n}from"./chunks/runtime-core.esm-bundler.NDZlvWPe.js";import{t as r}from"./chunks/plugin-vue_export-helper.BFm-5gVH.js";const i=JSON.parse(`{"title":"Security Policy","description":"","frontmatter":{"title":"Security Policy","outline":"deep","head":[["meta",{"property":"og:title","content":"Security Policy"}],["meta",{"name":"twitter:title","content":"Security Policy"}],["meta",{"property":"og:description","content":"A collection of high-performance JavaScript tools written in Rust"}],["meta",{"name":"twitter:description","content":"A collection of high-performance JavaScript tools written in Rust"}],["meta",{"property":"og:url","content":"https://oxc.rs/docs/contribute/security.html"}]]},"headers":[],"relativePath":"docs/contribute/security.md","filePath":"docs/contribute/security.md"}`);var a={name:`docs/contribute/security.md`};function o(r,i,a,o,s,c){return e(),t(`div`,null,[...i[0]||=[n(`<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /docs/contribute/security.md for this page in Markdown format</div><h1 id="security-policy" tabindex="-1">Security Policy <a class="header-anchor" href="#security-policy" aria-label="Permalink to “Security Policy”">​</a></h1><p>The following security policies are applied to all projects within the <a href="https://github.com/oxc-project" target="_blank" rel="noreferrer">oxc-project</a> organization.</p><p>Please inform <a href="https://github.com/Boshen" target="_blank" rel="noreferrer">@boshen</a> if you notice any oversights.</p><p><a href="https://www.npmjs.com/~boshen" target="_blank" rel="noreferrer">https://www.npmjs.com/~boshen</a> and <a href="https://crates.io/users/Boshen" target="_blank" rel="noreferrer">https://crates.io/users/Boshen</a> are the only accounts with publish access to our packages and crates.</p><h2 id="github-com" tabindex="-1">github.com <a class="header-anchor" href="#github-com" aria-label="Permalink to “github.com”">​</a></h2><ul><li>Required two-factor authentication for everyone in the organization <ul><li>Only secure two-factor methods are allowed</li></ul></li><li>Enabled GitHub Security Scanning, including secret scanning</li><li>GitHub Actions: Required all actions to be pinned to a full-length commit SHA</li><li>Enabled release immutability — assets and tags cannot be modified once a release is published</li><li>Required signed commits: <a href="https://docs.github.com/en/authentication/managing-commit-signature-verification/signing-commits" target="_blank" rel="noreferrer">https://docs.github.com/en/authentication/managing-commit-signature-verification/signing-commits</a><ul><li>Not enforced in repository settings; otherwise external contributors would not be able to contribute</li></ul></li><li>Long-lived tokens are not stored for publishing — see trusted publishing for <a href="http://npmjs.com" target="_blank" rel="noreferrer">npmjs.com</a> and <a href="http://crates.io" target="_blank" rel="noreferrer">crates.io</a> below</li><li>Enabled Renovate Bot for security updates</li><li>Using <a href="https://docs.zizmor.sh" target="_blank" rel="noreferrer">https://docs.zizmor.sh</a> to lint GitHub Actions for common security issues</li></ul><h2 id="npmjs-com" tabindex="-1">npmjs.com <a class="header-anchor" href="#npmjs-com" aria-label="Permalink to “npmjs.com”">​</a></h2><ul><li>Enforced 2FA for login</li><li>Published with <code>npm publish --provenance</code>: <a href="https://docs.npmjs.com/generating-provenance-statements" target="_blank" rel="noreferrer">https://docs.npmjs.com/generating-provenance-statements</a></li><li>Published with trusted publishing: <a href="https://docs.npmjs.com/trusted-publishers" target="_blank" rel="noreferrer">https://docs.npmjs.com/trusted-publishers</a></li><li>Installed Socket Security</li><li>Enabled Renovate Bot&#39;s <code>&quot;minimumReleaseAge&quot;: &quot;3 days&quot;</code> to avoid updating packages released within the past 3 days</li><li>Uses pnpm: <a href="https://pnpm.io/supply-chain-security" target="_blank" rel="noreferrer">https://pnpm.io/supply-chain-security</a><ul><li>No automatic <code>postinstall</code> scripts</li></ul></li></ul><h2 id="crates-io" tabindex="-1">crates.io <a class="header-anchor" href="#crates-io" aria-label="Permalink to “crates.io”">​</a></h2><ul><li>Published with trusted publishing: <a href="https://crates.io/docs/trusted-publishing" target="_blank" rel="noreferrer">https://crates.io/docs/trusted-publishing</a></li><li>Using <code>cargo deny</code> to check dependencies against the Rust advisory database (<a href="https://rustsec.org" target="_blank" rel="noreferrer">https://rustsec.org</a>).</li></ul>`,11)]])}var s=r(a,[[`render`,o]]);export{i as __pageData,s as default};