import{O as e,p as t,u as n}from"./chunks/runtime-core.esm-bundler.CAUM87s6.js";import{t as r}from"./chunks/plugin-vue_export-helper.C1_mtLIm.js";const i=JSON.parse(`{"title":"Ignore files","description":"","frontmatter":{"head":[["meta",{"property":"og:title","content":"Ignore files"}],["meta",{"name":"twitter:title","content":"Ignore files"}],["meta",{"property":"og:description","content":"A collection of high-performance JavaScript tools written in Rust"}],["meta",{"name":"twitter:description","content":"A collection of high-performance JavaScript tools written in Rust"}],["meta",{"property":"og:url","content":"https://oxc.rs/docs/guide/usage/formatter/ignore-files.html"}]]},"headers":[],"relativePath":"docs/guide/usage/formatter/ignore-files.md","filePath":"docs/guide/usage/formatter/ignore-files.md"}`);var a={name:`docs/guide/usage/formatter/ignore-files.md`};function o(r,i,a,o,s,c){return e(),n(`div`,null,[...i[0]||=[t(`<div style="display:none;" hidden="true" aria-hidden="true">Are you an LLM? You can read better optimized documentation at /docs/guide/usage/formatter/ignore-files.md for this page in Markdown format</div><h1 id="ignore-files" tabindex="-1">Ignore files <a class="header-anchor" href="#ignore-files" aria-label="Permalink to “Ignore files”">​</a></h1><p>Oxfmt provides several ways to ignore files.</p><h3 id="gitignore" tabindex="-1"><code>.gitignore</code> <a class="header-anchor" href="#gitignore" aria-label="Permalink to “.gitignore”">​</a></h3><p>Respects <code>.gitignore</code> in the current working directory and subdirectories.</p><p>Does not respect global, exclude, or <code>.gitignore</code> in parent directories. Does not require <code>.git</code> directory to exist.</p><p>Files listed here can still be formatted if explicitly specified. This is safe for use cases like <code>husky</code>, as ignored files are never staged.</p><h3 id="prettierignore-oxfmtrc-ignorepatterns" tabindex="-1"><code>.prettierignore</code> / <code>oxfmtrc.ignorePatterns</code> <a class="header-anchor" href="#prettierignore-oxfmtrc-ignorepatterns" aria-label="Permalink to “.prettierignore / oxfmtrc.ignorePatterns”">​</a></h3><p>These are formatter-specific ignore settings, separate from Git, and each operates within its own scope.</p><p><code>.prettierignore</code> is only read from the current working directory. For <code>.oxfmtrc.json(c)</code>, see <a href="./config.html">Configuration</a>.</p><p>The syntax is the same as <code>.gitignore</code>, and paths are resolved relative to the directory containing the ignore file.</p><p>Files ignored here cannot be formatted even if explicitly specified. This behavior is intended for use cases like <code>husky</code>.</p><p>You can also specify custom ignore paths with <code>--ignore-path</code>, or use <code>!</code>-prefixed positional paths to exclude files.</p><h3 id="vcs-directories-and-node-modules" tabindex="-1">VCS directories and <code>node_modules</code> <a class="header-anchor" href="#vcs-directories-and-node-modules" aria-label="Permalink to “VCS directories and node_modules”">​</a></h3><p>Directories like <code>.git</code>, <code>.svn</code> and <code>.jj</code> are ignored by default.</p><p>The <code>node_modules</code> directory is also ignored unless <code>--with-node_modules</code> flag is specified.</p><p>If the current working directory is inside these directories, formatting is still possible.</p><h3 id="lock-files" tabindex="-1">Lock files <a class="header-anchor" href="#lock-files" aria-label="Permalink to “Lock files”">​</a></h3><p>Files like <code>package-lock.json</code> and <code>pnpm-lock.yaml</code> are ignored by default.</p><p>These cannot be formatted even if explicitly specified.</p>`,20)]])}var s=r(a,[[`render`,o]]);export{i as __pageData,s as default};