import{O as e,p as t,u as n}from"./chunks/runtime-core.esm-bundler.aVieqAnv.js";import{t as r}from"./chunks/plugin-vue_export-helper.Bfcfpqzz.js";const i=JSON.parse(`{"title":"","description":"","frontmatter":{"search":false,"head":[["meta",{"property":"og:description","content":"A collection of high-performance JavaScript tools written in Rust"}],["meta",{"name":"twitter:description","content":"A collection of high-performance JavaScript tools written in Rust"}],["meta",{"property":"og:url","content":"https://oxc.rs/docs/guide/usage/linter/generated-cli.html"}]]},"headers":[],"relativePath":"docs/guide/usage/linter/generated-cli.md","filePath":"docs/guide/usage/linter/generated-cli.md"}`);var a={name:`docs/guide/usage/linter/generated-cli.md`};function o(r,i,a,o,s,c){return e(),n(`div`,null,[...i[0]||=[t(`<h2 id="usage" tabindex="-1">Usage <a class="header-anchor" href="#usage" aria-label="Permalink to “Usage”">​</a></h2><p><strong><code>oxlint</code></strong> [<strong><code>-c</code></strong>=<em><code>&lt;./.oxlintrc.json&gt;</code></em>] [<em><code>PATH</code></em>]...</p><h2 id="basic-configuration" tabindex="-1">Basic Configuration <a class="header-anchor" href="#basic-configuration" aria-label="Permalink to “Basic Configuration”">​</a></h2><ul><li><strong><code>-c</code></strong>, <strong><code>--config</code></strong>=<em><code>&lt;./.oxlintrc.json&gt;</code></em> — Oxlint configuration file</li></ul><ul><li><p>only <code>.json</code> extension is supported</p></li><li><p>you can use comments in configuration files.</p></li><li><p>tries to be compatible with ESLint v8&#39;s format</p><p>If not provided, Oxlint will look for <code>.oxlintrc.json</code> in the current working directory.</p></li></ul><ul><li><strong><code>--tsconfig</code></strong>=<em><code>&lt;./tsconfig.json&gt;</code></em> — TypeScript <code>tsconfig.json</code> path for reading path alias and project references for import plugin. If not provided, will look for <code>tsconfig.json</code> in the current working directory.</li><li><strong><code>--init</code></strong> — Initialize oxlint configuration with default values</li></ul><h2 id="allowing-denying-multiple-lints" tabindex="-1">Allowing / Denying Multiple Lints <a class="header-anchor" href="#allowing-denying-multiple-lints" aria-label="Permalink to “Allowing / Denying Multiple Lints”">​</a></h2><p>Accumulate rules and categories from left to right on the command-line.</p><p>For example <code>-D correctness -A no-debugger</code> or <code>-A all -D no-debugger</code>. The categories are:</p><ul><li><code>correctness</code> - Code that is outright wrong or useless (default)</li><li><code>suspicious</code> - Code that is most likely wrong or useless</li><li><code>pedantic</code> - Lints which are rather strict or have occasional false positives</li><li><code>perf</code> - Code that could be written in a more performant way</li><li><code>style</code> - Code that should be written in a more idiomatic way</li><li><code>restriction</code> - Lints which prevent the use of language and library features</li><li><code>nursery</code> - New lints that are still under development</li><li><code>all</code> - All categories listed above except <code>nursery</code>. Does not enable plugins automatically.</li></ul><p>Arguments:</p><ul><li><strong><code>-A</code></strong>, <strong><code>--allow</code></strong>=<em><code>NAME</code></em> — Allow the rule or category (suppress the lint)</li><li><strong><code>-W</code></strong>, <strong><code>--warn</code></strong>=<em><code>NAME</code></em> — Deny the rule or category (emit a warning)</li><li><strong><code>-D</code></strong>, <strong><code>--deny</code></strong>=<em><code>NAME</code></em> — Deny the rule or category (emit an error)</li></ul><h2 id="enable-disable-plugins" tabindex="-1">Enable/Disable Plugins <a class="header-anchor" href="#enable-disable-plugins" aria-label="Permalink to “Enable/Disable Plugins”">​</a></h2><ul><li><strong><code>--disable-unicorn-plugin</code></strong> — Disable unicorn plugin, which is turned on by default</li><li><strong><code>--disable-oxc-plugin</code></strong> — Disable oxc unique rules, which is turned on by default</li><li><strong><code>--disable-typescript-plugin</code></strong> — Disable TypeScript plugin, which is turned on by default</li><li><strong><code>--import-plugin</code></strong> — Enable import plugin and detect ESM problems. It is recommended to use alongside the <code>--tsconfig</code> option.</li><li><strong><code>--react-plugin</code></strong> — Enable react plugin, which is turned off by default</li><li><strong><code>--jsdoc-plugin</code></strong> — Enable jsdoc plugin and detect JSDoc problems</li><li><strong><code>--jest-plugin</code></strong> — Enable the Jest plugin and detect test problems</li><li><strong><code>--vitest-plugin</code></strong> — Enable the Vitest plugin and detect test problems</li><li><strong><code>--jsx-a11y-plugin</code></strong> — Enable the JSX-a11y plugin and detect accessibility problems</li><li><strong><code>--nextjs-plugin</code></strong> — Enable the Next.js plugin and detect Next.js problems</li><li><strong><code>--react-perf-plugin</code></strong> — Enable the React performance plugin and detect rendering performance problems</li><li><strong><code>--promise-plugin</code></strong> — Enable the promise plugin and detect promise usage problems</li><li><strong><code>--node-plugin</code></strong> — Enable the node plugin and detect node usage problems</li><li><strong><code>--regex-plugin</code></strong> — Enable the regex plugin and detect regex usage problems</li><li><strong><code>--vue-plugin</code></strong> — Enable the vue plugin and detect vue usage problems</li></ul><h2 id="fix-problems" tabindex="-1">Fix Problems <a class="header-anchor" href="#fix-problems" aria-label="Permalink to “Fix Problems”">​</a></h2><ul><li><strong><code>--fix</code></strong> — Fix as many issues as possible. Only unfixed issues are reported in the output.</li><li><strong><code>--fix-suggestions</code></strong> — Apply auto-fixable suggestions. May change program behavior.</li><li><strong><code>--fix-dangerously</code></strong> — Apply dangerous fixes and suggestions</li></ul><h2 id="ignore-files" tabindex="-1">Ignore Files <a class="header-anchor" href="#ignore-files" aria-label="Permalink to “Ignore Files”">​</a></h2><ul><li><p><strong><code>--ignore-path</code></strong>=<em><code>PATH</code></em> — Specify the file to use as your <code>.eslintignore</code></p></li><li><p><strong><code>--ignore-pattern</code></strong>=<em><code>PAT</code></em> — Specify patterns of files to ignore (in addition to those in <code>.eslintignore</code>)</p><p>The supported syntax is the same as for <code>.eslintignore</code> and <code>.gitignore</code> files. You should quote your patterns in order to avoid shell interpretation of glob patterns.</p></li><li><p><strong><code>--no-ignore</code></strong> — Disable excluding files from <code>.eslintignore</code> files, <strong><code>--ignore-path</code></strong> flags and <strong><code>--ignore-pattern</code></strong> flags</p></li></ul><div class="note custom-block github-alert"><p class="custom-block-title">NOTE</p><p><code>.gitignore</code> is only respected inside a Git repository.</p></div><h2 id="handle-warnings" tabindex="-1">Handle Warnings <a class="header-anchor" href="#handle-warnings" aria-label="Permalink to “Handle Warnings”">​</a></h2><ul><li><strong><code>--quiet</code></strong> — Disable reporting on warnings, only errors are reported</li><li><strong><code>--deny-warnings</code></strong> — Ensure warnings produce a non-zero exit code</li><li><strong><code>--max-warnings</code></strong>=<em><code>INT</code></em> — Specify a warning threshold, which can be used to force exit with an error status if there are too many warning-level rule violations in your project</li></ul><h2 id="output" tabindex="-1">Output <a class="header-anchor" href="#output" aria-label="Permalink to “Output”">​</a></h2><ul><li><strong><code>-f</code></strong>, <strong><code>--format</code></strong>=<em><code>ARG</code></em> — Use a specific output format. Possible values: <code>checkstyle</code>, <code>default</code>, <code>github</code>, <code>gitlab</code>, <code>json</code>, <code>junit</code>, <code>stylish</code>, <code>unix</code></li></ul><h2 id="miscellaneous" tabindex="-1">Miscellaneous <a class="header-anchor" href="#miscellaneous" aria-label="Permalink to “Miscellaneous”">​</a></h2><ul><li><strong><code>--silent</code></strong> — Do not display any diagnostics</li><li><strong><code>--threads</code></strong>=<em><code>INT</code></em> — Number of threads to use. Set to 1 for using only 1 CPU core.</li><li><strong><code>--print-config</code></strong> — This option outputs the configuration to be used. When present, no linting is performed and only config-related options are valid.</li></ul><h2 id="inline-configuration-comments" tabindex="-1">Inline Configuration Comments <a class="header-anchor" href="#inline-configuration-comments" aria-label="Permalink to “Inline Configuration Comments”">​</a></h2><ul><li><strong><code>--report-unused-disable-directives</code></strong> — Report directive comments like <code>// oxlint-disable-line</code>, when no errors would have been reported on that line anyway</li><li><strong><code>--report-unused-disable-directives-severity</code></strong>=<em><code>SEVERITY</code></em> — Same as <code>--report-unused-disable-directives</code>, but allows you to specify the severity level of the reported errors. Only one of these two options can be used at a time.</li></ul><h2 id="available-positional-items" tabindex="-1">Available positional items: <a class="header-anchor" href="#available-positional-items" aria-label="Permalink to “Available positional items:”">​</a></h2><ul><li><em><code>PATH</code></em> — Single file, single path or list of paths</li></ul><h2 id="available-options" tabindex="-1">Available options: <a class="header-anchor" href="#available-options" aria-label="Permalink to “Available options:”">​</a></h2><ul><li><strong><code>--rules</code></strong> — List all the rules that are currently registered</li><li><strong><code>--lsp</code></strong> — Start the language server</li><li><strong><code>--disable-nested-config</code></strong> — Disable the automatic loading of nested configuration files</li><li><strong><code>--type-aware</code></strong> — Enable rules that require type information</li><li><strong><code>--type-check</code></strong> — Enable experimental type checking (includes TypeScript compiler diagnostics)</li><li><strong><code>-h</code></strong>, <strong><code>--help</code></strong> — Prints help information</li><li><strong><code>-V</code></strong>, <strong><code>--version</code></strong> — Prints version information</li></ul>`,31)]])}var s=r(a,[[`render`,o]]);export{i as __pageData,s as default};